/** PDO TRANSACTION */
/*fixme: check this, or add tpl */
 $querySelect="SELECT * FROM table1 where id_user= :id_user";
 $queryInsert="INSERT INTO table2 (val1, val2) VALUES(:num1,:color1), (:num2,:color2)";
 $pdo->beginTransaction();//начало
    try {
    $stmt = $pdo->prepare($querySelect);
    $stmt->execute(array(":id_user"=>25));
    $res1=$stmt->fetchAll();    

    $stmt = $pdo->prepare($queryInsert);
    $stmt->execute(array(":num1"=>1,":color1"=>'red',":num2"=>2,":color2"=>'green'));
    $rew2=$stmt->fetchAll();
    } catch (Exception $e) {
            $pdo->rollBack();//откат
            header('Content-Type: application/json', true, 500);
            echo json_encode(["result:" => "db_error!", "request" => $e->getMessage()]);
    }

    $pdo->commit();//фиксация
/** End of pdo transacction */
